import React from 'react'
import MyContext from "../../context/MyContext";
import { useContext } from "react";
import ShowQuestion from '../ShowQuestion';
const ShowChoices = (question) => {
    const { nextQuestion,buttonClass } = useContext(MyContext)
    question.question[nextQuestion - 1].answers
        .sort(() => 0.5 - Math.random())
        .map((ans, i) => {
            return (
                <button key={i} onClick={answerButtonHandler} className={buttonClass}>
                    {ans}
                </button>
            );
        });
}
export default ShowChoices
