import { useContext } from "react";
import MyContext from "../context/MyContext";
import Ask from "./Ask";
import QuestionContainer from "./QuestionContainer";

const Quiz = () => {
  const startButtonHandle = () => {
    setQShow(true);
  };
  const {showQ,setQShow}=useContext(MyContext)
  return (
    <div className="container flex">
      <div className={showQ? "flex padding" :"flex"}>

        <Ask />
        <button onClick={startButtonHandle}>Start New Quiz</button>
      </div>

      {!showQ && <p>Choose how many questions you want to answer</p>}
      {showQ && <QuestionContainer />}
    </div>
  );
};

export default Quiz;
