import React from "react";
import { useContext } from "react";
import MyContext from "../context/MyContext";
const QuestionContainer = () => {
  const { questionsArray } = useContext(MyContext);

  const showQuestion = questionsArray.map((questionObj, i) => {
   return <div>
       <p>{questionObj.question}{ }</p>
      <div className="answerButton flex">
        <button class="button">{questionObj.answers[0]}</button>
        <button class="button">{questionObj.answers[1]}</button>
        <button class="button">{questionObj.answers[2]}</button>
        <button class="button">{questionObj.answers[3]}</button>
      </div>
    </div>;
  });
  return (
      <div className="questionContainer flex">
          {showQuestion}
      <button>Next</button>
    </div>
  );
};

export default QuestionContainer;
