import { useState, useEffect } from "react";

import MyContext from "./MyContext";
const MyProvider = ({ children }) => {
  const [nextQuestion, setNextQuestion] = useState(1);
  const questionsArray = [
    {
      question: "Choose the right answer",
      answers: [
        "This first answer is the only right answer",
        "This is an ancient rite",
        "A wright is a skilled maker of objects",
        "You have forfeited the right to do this",
      ],
    },
    {
      question: "Which answer is the right one?",
      answers: [
        "I am the one. Pick me! Pick me!",
        "I was number two in the JSON file",
        "We, the people, but three's a crowd",
        "Four tunes favour the bold",
      ],
    },
    {
      question: "Choose the answer with the check mark?",
      answers: ["✓ Check!", "♚♕♖ Checkmate!", "× cross", "⇨ right"],
    },
  ];
  const[buttonClass,setButtonClass]=useState("button")
  const answerButtonHandler = (e, id) => {
    setSelected(true);
    // console.log(e.target)
    // e.target.classList.add("selectedButton")

    if (id === e.target.id) {
      setButtonClass("selectedButton button")
    }

  }

  const answerButtons = questionsArray[nextQuestion - 1].answers.map(
    (ans, i) => {

      return (
        <button id={i} onClick={answerButtonHandler} className= {buttonClass}>
          {ans}
        </button>
      );
    }
  );
  const showQuestion = (
    <div>
      <p>
        {questionsArray[nextQuestion - 1].question}
        {nextQuestion}
      </p>
      <div className="answerButton flex">{answerButtons}</div>
    </div>
  );

  const nextQHandler = () => {
    if (nextQuestion < questionsArray.length) {
      setNextQuestion((nextQuestion) => nextQuestion + 1);
      setSelected(false);
    }
  };
  // useEffect(() => {

  //   return () => {

  //   };
  // }, [nextQuestion])
  const [selected, setSelected] = useState(false);
  return (
    <MyContext.Provider
      value={{
        questionsArray,
        nextQuestion,
        setNextQuestion,
        showQuestion,
        nextQHandler,
        selected,
        setSelected,
      }}
    >
      {children}
    </MyContext.Provider>
  );
};

export default MyProvider;
