      <div>
        <p className="padding">
          {randomQuestionsArray[nextQuestion - 1].question}
          {nextQuestion}
      </p>
      <div>< ShowChoices question={randomQuestionsArray}/></div>

      </div>
    );
}

export default ShowQuestion
