           {questionsArray[nextQuestion - 1].answers[nextQuestion - 1]}
         </button>
       </div>
     </div>
   );

    const nextQHandler = () => {

            if (nextQuestion < questionsArray.length) {
              setNextQuestion((nextQuestion) => nextQuestion + 1)
              setSelected
           }
    }
    // useEffect(() => {

