    // }, [nextQuestion])
  const [selected,setSelected]=useState(false)
    return (
        <MyContext.Provider value={{questionsArray,nextQuestion,setNextQuestion,showQuestion,nextQHandler,selected,setSelected}} >
            {children}
        </MyContext.Provider>
    )
}

export default MyProvider
